  zoekDagenVooruit: 7,
  zoekDagenTerug: 3
};

// Input data van webhook - data zit in body!
const input = $input.first().json;
const webhookBody = input.body || input; // Fallback voor pinned data

// Valideer
if (!webhookBody.dealId) throw new Error('dealId is vereist');
if (!webhookBody.preferences || !webhookBody.preferences.length) throw new Error('preferences zijn vereist');
if (!webhookBody.customerAddress) throw new Error('customerAddress is vereist');

return [{
  json: {
    dealId: webhookBody.dealId,
    contactEmail: webhookBody.contactEmail,
    customerAddress: webhookBody.customerAddress,
    preferences: webhookBody.preferences,
    monteurs: MONTEURS,
    config: CONFIG
  }
}];

// Bereid data voor calendar API calls
const prevData = $('Config + Validatie').first().json;
const dealResponse = $input.first().json;

// Converteer type_duur + installatie_duur naar minuten
function berekenInstallatieDuurMinuten(typeDuur, aantal) {
  const value = parseInt(aantal) || 0;
  const multipliers = {
    'minuten': 1,
    'uren': 60,
    'dagen': 540,    // 9 werkuren per dag (08:00-17:00)
    'weken': 2700    // 5 werkdagen x 9 uur = 45 uur
  };
  const multiplier = multipliers[typeDuur?.toLowerCase()] || 1;
  return value * multiplier;
}

const typeDuur = dealResponse.properties?.type_duur;
const aantalDuur = dealResponse.properties?.installatie_duur;
const installatieDuur = berekenInstallatieDuurMinuten(typeDuur, aantalDuur) || prevData.config.defaultInstallatieDuur;

// Helper: Parse Dutch date format ("29 jan", "2 feb") to ISO date
function parseDutchDate(dateStr) {
  const months = {
    'jan': '01', 'feb': '02', 'mrt': '03', 'apr': '04',
    'mei': '05', 'jun': '06', 'jul': '07', 'aug': '08',
    'sep': '09', 'okt': '10', 'nov': '11', 'dec': '12'
  };

  // If already ISO format, return as-is
  if (dateStr.match(/^\d{4}-\d{2}-\d{2}$/)) {
    return dateStr;
  }

  // Parse Dutch format: "29 jan", "2 feb"
  const match = dateStr.toLowerCase().match(/^(\d{1,2})\s+(\w{3})$/);
  if (match) {
    const day = match[1].padStart(2, '0');
    const monthStr = match[2];
    const month = months[monthStr];
    if (month) {
      const now = new Date();
      let year = now.getFullYear();
      const testDate = new Date(`${year}-${month}-${day}`);
      if (testDate < now) {
        year++;
      }
      return `${year}-${month}-${day}`;
    }
  }

  const d = new Date(dateStr);
  if (!isNaN(d.getTime())) {
    return d.toISOString().split('T')[0];
  }

  throw new Error(`Kan datum niet parsen: ${dateStr}`);
}

// Parse en sorteer de datums
const dates = prevData.preferences.map(p => parseDutchDate(p.date)).sort();
const startDate = dates[0];
const endDate = dates[dates.length - 1];

// UITGEBREIDE RANGE: -3 dagen terug, +7 dagen vooruit voor gap-finding
const zoekTerug = prevData.config.zoekDagenTerug || 3;
const zoekVooruit = prevData.config.zoekDagenVooruit || 7;

const extendedStartDate = new Date(startDate);
extendedStartDate.setDate(extendedStartDate.getDate() - zoekTerug);
const extendedStartStr = extendedStartDate.toISOString().split('T')[0];

const extendedEndDate = new Date(endDate);
extendedEndDate.setDate(extendedEndDate.getDate() + zoekVooruit + 1);
const extendedEndStr = extendedEndDate.toISOString().split('T')[0];

const tz = '+01:00'; // CET
const calendarQueryStart = `${extendedStartStr}T00:00:00${tz}`;
const calendarQueryEnd = `${extendedEndStr}T00:00:00${tz}`;

const preferencesWithIsoDates = prevData.preferences.map(p => ({
  ...p,
  date: parseDutchDate(p.date)
}));

const items = prevData.monteurs.map((monteur, index) => ({
  json: {
    ...prevData,
    preferences: preferencesWithIsoDates,
    deal: {
      id: dealResponse.id,
      name: dealResponse.properties?.dealname,
      amount: dealResponse.properties?.amount,
      stage: dealResponse.properties?.dealstage
    },
    installatieDuurMinuten: installatieDuur,
    calendarQueryStart,
    calendarQueryEnd,
    currentMonteur: monteur,
    currentMonteurIndex: index
  }
}));

return items;

// Verzamel alle calendar events van alle monteurs (parallel verwerkt)
// Input items bevatten calendar results gekoppeld aan hun input data
const allItems = $input.all();
const allInputItems = $('Bereid Calendar Queries').all();
const firstItem = allInputItems[0].json;

// Helper: Haal adres uit calendar event
function getAddressFromEvent(event) {
  // Probeer eerst location.displayName
  if (event.location?.displayName && event.location.displayName.trim()) {
    return event.location.displayName.trim();
  }

  // Anders uit body content parsen
  // Format: "Adres:\nStraat\nPostcode Stad"
  const body = event.body?.content || '';
  const adresMatch = body.match(/Adres:\s*\n([^\n]+)\s*\n([^\n]+)/i);
  if (adresMatch) {
    return `${adresMatch[1].trim()}, ${adresMatch[2].trim()}`;
  }

  return null;
}

// Map calendar events per monteur met adres extractie
const calendarEventsPerMonteur = {};

// Debug logging
const debugInfo = {
  totalInputItems: allItems.length,
  totalQueryItems: allInputItems.length,
  monteurIds: []
};

allItems.forEach((result, index) => {
  const inputItem = allInputItems[index]?.json;
  const monteurId = inputItem?.currentMonteur?.id || `monteur${index + 1}`;
  const events = result.json?.value || [];

  debugInfo.monteurIds.push(monteurId);

  // Voeg extracted address toe aan elk event
  const eventsWithAddress = events.map(event => ({
    ...event,
    extractedAddress: getAddressFromEvent(event)
  }));

  calendarEventsPerMonteur[monteurId] = eventsWithAddress;
});

return [{
  json: {
    dealId: firstItem.dealId,
    contactEmail: firstItem.contactEmail,
    customerAddress: firstItem.customerAddress,
    preferences: firstItem.preferences,
    monteurs: firstItem.monteurs,
    config: firstItem.config,
    deal: firstItem.deal,
    installatieDuurMinuten: firstItem.installatieDuurMinuten,
    calendarEventsPerMonteur,
    _debug: debugInfo
  }
}];

// =====================================================
// SCORING ALGORITME v3 - MET REISTIJD VANAF VORIGE AFSPRAAK
// Berekent reistijd tussen afspraken, niet alleen vanaf thuis
// =====================================================

const baseData = $('Merge Calendar Data').first().json;
const distanceResponse = $input.first().json;

const { preferences, monteurs, calendarEventsPerMonteur, installatieDuurMinuten, config, customerAddress, deal } = baseData;

// =====================================================
// REISTIJD LOOKUP TABEL (geschatte minuten tussen steden)
// =====================================================
const REISTIJD_MATRIX = {
  'apeldoorn_den bosch': 75, 'apeldoorn_s-hertogenbosch': 75,
  'amersfoort_den bosch': 55, 'amersfoort_s-hertogenbosch': 55,
  'amsterdam_den bosch': 80, 'amsterdam_s-hertogenbosch': 80,
  'utrecht_den bosch': 45, 'utrecht_s-hertogenbosch': 45,
  'eindhoven_den bosch': 35, 'eindhoven_s-hertogenbosch': 35,
  'veldhoven_den bosch': 40, 'veldhoven_s-hertogenbosch': 40,
  'berlicum_den bosch': 10, 'berlicum_s-hertogenbosch': 10,
  'arnhem_den bosch': 55, 'arnhem_s-hertogenbosch': 55,
  'rotterdam_den bosch': 55, 'rotterdam_s-hertogenbosch': 55,
  'tilburg_den bosch': 25, 'tilburg_s-hertogenbosch': 25,
  'breda_den bosch': 40, 'breda_s-hertogenbosch': 40,
  'nijmegen_den bosch': 45, 'nijmegen_s-hertogenbosch': 45,
  'den haag_den bosch': 70, 'den haag_s-hertogenbosch': 70,
  'hoofddorp_den bosch': 75, 'hoofddorp_s-hertogenbosch': 75,
  'delft_den bosch': 65, 'delft_s-hertogenbosch': 65,
  'haarlem_den bosch': 85, 'haarlem_s-hertogenbosch': 85,
  'bloemendaal_den bosch': 90, 'bloemendaal_s-hertogenbosch': 90,
  'apeldoorn_berlicum': 70, 'apeldoorn_amersfoort': 45,
  'amsterdam_berlicum': 75, 'amsterdam_amersfoort': 45,
  'veldhoven_berlicum': 35, 'eindhoven_berlicum': 30,
  'arnhem_amersfoort': 50, 'utrecht_amersfoort': 25,
  'rotterdam_amersfoort': 60, 'tilburg_amersfoort': 70,
  'spijkenisse_den bosch': 65, 'capelle aan den ijssel_den bosch': 55,
  'etten-leur_den bosch': 50, 'sittard_den bosch': 90,
  'herten_den bosch': 85, 'putten_den bosch': 70,
  'nieuwleusen_den bosch': 95, 'pijnacker_den bosch': 60,
  'amstelveen_den bosch': 75, 'bergeijk_den bosch': 50
};

// Helper: Haal stad uit een locatie string of adres
function extractCity(locationStr) {
  if (!locationStr) return null;
  const str = locationStr.toLowerCase().trim();

  // Bekende steden om te matchen
  const cities = ['apeldoorn', 'amersfoort', 'amsterdam', 'utrecht', 'eindhoven', 
    'veldhoven', 'berlicum', 'arnhem', 'rotterdam', 'tilburg', 'breda', 'nijmegen',
    'den haag', 'hoofddorp', 'delft', 'haarlem', 'bloemendaal', 'den bosch',
    's-hertogenbosch', 'spijkenisse', 'capelle aan den ijssel', 'etten-leur',
    'sittard', 'herten', 'putten', 'nieuwleusen', 'pijnacker', 'amstelveen', 'bergeijk'];

  for (const city of cities) {
    if (str.includes(city)) return city;
  }
  return null;
}

// Helper: Haal stad uit calendar event
function extractCityFromEvent(event) {
  // Probeer uit subject (format: " Apeldoorn | Groepenkast vervangen")
  const subjectMatch = event.subject?.match(/^\s*([A-Za-z\s\-']+)\s*\|/);
  if (subjectMatch) {
    const city = extractCity(subjectMatch[1]);
    if (city) return city;
  }

  // Probeer uit location
  if (event.location?.displayName) {
    const city = extractCity(event.location.displayName);
    if (city) return city;
  }

  // Probeer uit extractedAddress
  if (event.extractedAddress) {
    const city = extractCity(event.extractedAddress);
    if (city) return city;
  }

  // Probeer uit body
  const bodyMatch = event.body?.content?.match(/(\d{4}\s*[A-Z]{2})\s+([A-Za-z\s\-']+)/);
  if (bodyMatch) {
    const city = extractCity(bodyMatch[2]);
    if (city) return city;
  }

  return null;
}

// Helper: Schat reistijd tussen twee steden
function getEstimatedTravelTime(fromCity, toCity) {
  if (!fromCity || !toCity) return 45; // default
  const from = fromCity.toLowerCase().trim();
  const to = toCity.toLowerCase().trim();
  if (from === to) return 10; // zelfde stad

  const key1 = `${from}_${to}`;
  const key2 = `${to}_${from}`;
  return REISTIJD_MATRIX[key1] || REISTIJD_MATRIX[key2] || 45; // default 45 min
}

// Helper: Vind vorige afspraak voor een bepaald tijdstip
function findPreviousEvent(events, slotStart, slotDate) {
  const sameDayEvents = events.filter(e => {
    const eventDate = new Date(e.start.dateTime).toISOString().split('T')[0];
    return eventDate === slotDate;
  });

  let previousEvent = null;
  let latestEnd = null;

  for (const event of sameDayEvents) {
    const eventEnd = new Date(event.end.dateTime);
    if (eventEnd <= slotStart) {
      if (!latestEnd || eventEnd > latestEnd) {
        latestEnd = eventEnd;
        previousEvent = event;
      }
    }
  }
  return previousEvent;
}

// Helper: Haal stad uit klantadres
function extractCustomerCity(address) {
  return extractCity(address) || 'den bosch'; // fallback
}

const customerCity = extractCustomerCity(customerAddress);

// Parse reistijden van Google Maps (thuis -> klant)
const reistijdenVanafThuis = {};
let minReistijd = Infinity;
if (distanceResponse.rows && distanceResponse.status === 'OK') {
  distanceResponse.rows.forEach((row, index) => {
    const monteur = monteurs[index];
    if (monteur && row.elements && row.elements[0]) {
      const element = row.elements[0];
      const reistijd = element.status === 'OK' ? Math.round(element.duration.value / 60) : 30;
      reistijdenVanafThuis[monteur.id] = reistijd;
      if (reistijd < minReistijd) minReistijd = reistijd;
    }
  });
} else {
  monteurs.forEach(m => { reistijdenVanafThuis[m.id] = 30; });
  minReistijd = 30;
}

// Helper: Check of tijdslot haalbaar is (overlap + reistijd van vorige afspraak)
function isSlotFeasible(monteurId, date, startTime, durationMinutes, monteur) {
  const events = calendarEventsPerMonteur[monteurId] || [];
  const slotStart = new Date(`${date}T${startTime}:00`);
  const slotEnd = new Date(slotStart.getTime() + durationMinutes * 60000);

  // Check 1: Directe overlap met bestaande afspraken
  for (const event of events) {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);
    if (slotStart < eventEnd && slotEnd > eventStart) {
      return { feasible: false, reason: 'overlap' };
    }
  }

  // Check 2: Kan monteur op tijd aankomen van vorige afspraak?
  const previousEvent = findPreviousEvent(events, slotStart, date);
  let effectiveReistijd = reistijdenVanafThuis[monteurId] || 30;

  if (previousEvent) {
    const previousEnd = new Date(previousEvent.end.dateTime);
    const previousCity = extractCityFromEvent(previousEvent);
    const travelTime = getEstimatedTravelTime(previousCity, customerCity);
    const bufferMinutes = config.bufferMinuten || 30;
    const earliestArrival = new Date(previousEnd.getTime() + (travelTime + bufferMinutes) * 60000);

    effectiveReistijd = travelTime; // Update voor scoring

    if (earliestArrival > slotStart) {
      return { 
        feasible: false, 
        reason: 'reistijd_te_kort',
        details: { 
          previousEnd: previousEnd.toISOString(),
          previousCity,
          travelTime,
          earliestArrival: earliestArrival.toISOString(),
          slotStart: slotStart.toISOString()
        }
      };
    }
  }

  return { feasible: true, effectiveReistijd };
}

// Helper: Oude isSlotAvailable voor backwards compatibility
function isSlotAvailable(monteurId, date, startTime, durationMinutes) {
  const result = isSlotFeasible(monteurId, date, startTime, durationMinutes, null);
  return result.feasible;
}

// Helper: Check werkuren
function isWithinWorkHours(startTime, durationMinutes, werkuren) {
  const [startH, startM] = startTime.split(':').map(Number);
  const startMinutes = startH * 60 + startM;
  const endMinutes = startMinutes + durationMinutes;
  const [werkStartH, werkStartM] = werkuren.start.split(':').map(Number);
  const [werkEndH, werkEndM] = werkuren.eind.split(':').map(Number);
  return startMinutes >= (werkStartH * 60 + werkStartM) && endMinutes <= (werkEndH * 60 + werkEndM);
}

// Helper: Tel werklast
function getWorkload(monteurId) {
  const events = calendarEventsPerMonteur[monteurId] || [];
  const now = new Date();
  const weekStart = new Date(now); weekStart.setDate(now.getDate() - now.getDay() + 1);
  const weekEnd = new Date(weekStart); weekEnd.setDate(weekStart.getDate() + 6);
  let jobs = 0, minutes = 0;
  events.forEach(event => {
    const eventDate = new Date(event.start.dateTime);
    if (eventDate >= weekStart && eventDate <= weekEnd) {
      jobs++;
      minutes += (new Date(event.end.dateTime) - new Date(event.start.dateTime)) / 60000;
    }
  });
  return { jobs, hours: minutes / 60 };
}

// Helper: Check of slot een gat opvult (efficiency bonus)
function checksGapFilling(monteurId, date, startTime, durationMinutes) {
  const events = calendarEventsPerMonteur[monteurId] || [];
  const slotStart = new Date(`${date}T${startTime}:00`);
  const slotEnd = new Date(slotStart.getTime() + durationMinutes * 60000);
  let adjacentBefore = false, adjacentAfter = false;
  for (const event of events) {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);
    if (Math.abs(eventEnd.getTime() - slotStart.getTime()) < 60 * 60000) adjacentBefore = true;
    if (Math.abs(slotEnd.getTime() - eventStart.getTime()) < 60 * 60000) adjacentAfter = true;
  }
  return adjacentBefore || adjacentAfter ? 20 : 0;
}

// NIEUW: Zoek gaten in calendars voor alle monteurs (met reistijd check)
function findCalendarGaps(referenceDate, zoekTerug, zoekVooruit) {
  const gaps = [];
  const refDate = new Date(referenceDate);
  const timeSlots = ['08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00'];

  for (let dayOffset = -zoekTerug; dayOffset <= zoekVooruit; dayOffset++) {
    const checkDate = new Date(refDate);
    checkDate.setDate(refDate.getDate() + dayOffset);
    const dayOfWeek = checkDate.getDay();
    if (dayOfWeek === 0 || dayOfWeek === 6) continue;
    const dateStr = checkDate.toISOString().split('T')[0];

    for (const monteur of monteurs) {
      const thuisReistijd = reistijdenVanafThuis[monteur.id] || 30;

      for (const timeSlot of timeSlots) {
        if (!isWithinWorkHours(timeSlot, installatieDuurMinuten + config.bufferMinuten, monteur.werkuren)) continue;

        // Gebruik isSlotFeasible i.p.v. isSlotAvailable
        const feasibility = isSlotFeasible(monteur.id, dateStr, timeSlot, installatieDuurMinuten + config.bufferMinuten, monteur);
        if (!feasibility.feasible) continue;

        const effectiveReistijd = feasibility.effectiveReistijd || thuisReistijd;

        // Filter op max reistijd
        if (effectiveReistijd > config.maxReistijdMinuten) continue;

        const workload = getWorkload(monteur.id);
        if (workload.jobs >= monteur.maxJobsPerWeek) continue;

        const gapBonus = checksGapFilling(monteur.id, dateStr, timeSlot, installatieDuurMinuten);
        const reistijdScore = Math.max(0, 50 - (effectiveReistijd / 2));
        const datumAfstand = Math.abs(dayOffset);
        const datumScore = Math.max(0, 30 - (datumAfstand * 3));
        const score = 100 + reistijdScore + datumScore + gapBonus;

        gaps.push({
          date: dateStr,
          timeSlot,
          monteur: { id: monteur.id, naam: monteur.naam, email: monteur.email, calendarId: monteur.calendarId },
          reistijdMinuten: effectiveReistijd,
          thuisReistijd,
          workload,
          score,
          dayOffset,
          isGapFiller: gapBonus > 0
        });
      }
    }
  }
  return gaps.sort((a, b) => b.score - a.score);
}

// STAP 1: Score klant-voorkeuren met reistijd vanaf vorige afspraak
const scoredOptions = [];
const failReasons = [];

for (const preference of preferences) {
  for (const monteur of monteurs) {
    const thuisReistijd = reistijdenVanafThuis[monteur.id] || 30;

    // Check 1: Werkuren
    if (!isWithinWorkHours(preference.timeSlot, installatieDuurMinuten + config.bufferMinuten, monteur.werkuren)) {
      failReasons.push({ reason: 'buiten_werkuren', preference, monteur: monteur.naam });
      continue;
    }

    // Check 2: Haalbaarheid (overlap + reistijd van vorige afspraak)
    const feasibility = isSlotFeasible(monteur.id, preference.date, preference.timeSlot, installatieDuurMinuten + config.bufferMinuten, monteur);
    if (!feasibility.feasible) {
      failReasons.push({ 
        reason: feasibility.reason, 
        preference, 
        monteur: monteur.naam,
        details: feasibility.details 
      });
      continue;
    }

    // Gebruik effectieve reistijd (vanaf vorige afspraak of thuis)
    const effectiveReistijd = feasibility.effectiveReistijd || thuisReistijd;

    // Check 3: Te ver weg? -> Volledig verbergen
    if (effectiveReistijd > config.maxReistijdMinuten) {
      failReasons.push({ reason: 'te_ver_weg', preference, monteur: monteur.naam, reistijd: effectiveReistijd });
      continue;
    }

    // Check 4: Werklast
    const workload = getWorkload(monteur.id);
    if (workload.jobs >= monteur.maxJobsPerWeek || workload.hours >= monteur.maxUrenPerWeek) {
      failReasons.push({ reason: 'werklast_vol', preference, monteur: monteur.naam });
      continue;
    }

    const gapBonus = checksGapFilling(monteur.id, preference.date, preference.timeSlot, installatieDuurMinuten);
    const score = 100 + Math.max(0, 50 - (effectiveReistijd / 2)) + Math.max(0, 30 - (workload.jobs * 2)) + gapBonus;

    scoredOptions.push({
      preference,
      monteur: { id: monteur.id, naam: monteur.naam, email: monteur.email, calendarId: monteur.calendarId },
      reistijdMinuten: effectiveReistijd,
      thuisReistijd,
      workload,
      score,
      matchType: 'preference',
      warning: null
    });
  }
}

scoredOptions.sort((a, b) => b.score - a.score);

// STAP 2: Als geen match op voorkeuren, zoek gaten
let matchType = 'preference';
let alternativeReason = null;
let allOptions = [...scoredOptions];

if (scoredOptions.length === 0) {
  matchType = 'alternative';
  const refDate = preferences[0]?.date || new Date().toISOString().split('T')[0];
  const gaps = findCalendarGaps(refDate, config.zoekDagenTerug || 3, config.zoekDagenVooruit || 7);

  if (gaps.length > 0) {
    alternativeReason = 'Uw voorkeursdatum was niet beschikbaar. Dit zijn de eerstvolgende mogelijkheden.';
    allOptions = gaps.map(g => ({ ...g, matchType: 'alternative' }));
  } else {
    alternativeReason = 'Geen beschikbaarheid in de komende ' + (config.zoekDagenVooruit || 7) + ' dagen.';
  }
}

// STAP 3: Bepaal redenen voor Discord
const diagnostics = {
  buitenReisgebied: minReistijd > config.maxReistijdMinuten,
  buitenWerkuren: failReasons.filter(r => r.reason === 'buiten_werkuren').length > 0,
  geenBeschikbaarheid: failReasons.filter(r => r.reason === 'niet_beschikbaar' || r.reason === 'overlap').length > 0,
  reistijdTekort: failReasons.filter(r => r.reason === 'reistijd_te_kort').length > 0,
  teVerWeg: failReasons.filter(r => r.reason === 'te_ver_weg').length > 0,
  monteurVolGeboekt: failReasons.filter(r => r.reason === 'werklast_vol').length > 0,
  failReasonsSummary: failReasons.slice(0, 10) // Voor debugging
};

// STAP 4: Bouw suggestions array (monteurs te ver weg zijn al gefilterd)
const suggestions = allOptions.slice(0, 5).map(opt => {
  const [startH, startM] = opt.timeSlot ? opt.timeSlot.split(':').map(Number) : opt.preference.timeSlot.split(':').map(Number);
  const totalMinutes = startH * 60 + startM + installatieDuurMinuten;
  const endTime = String(Math.floor(totalMinutes / 60)).padStart(2, '0') + ':' + String(totalMinutes % 60).padStart(2, '0');

  return {
    date: opt.date || opt.preference?.date,
    timeSlot: opt.timeSlot || opt.preference?.timeSlot,
    endTime,
    period: startH < 12 ? 'ochtend' : 'middag',
    monteur: opt.monteur,
    score: opt.score,
    reistijdMinuten: opt.reistijdMinuten,
    matchType: opt.matchType || matchType,
    warning: null, // Warnings niet meer nodig - te verre monteurs worden gefilterd
    isGapFiller: opt.isGapFiller || false
  };
});

return [{
  json: {
    dealId: baseData.dealId,
    contactEmail: baseData.contactEmail,
    customerAddress,
    deal,
    installatieDuurMinuten,
    config,
    scoredOptions: allOptions,
    besteOptie: allOptions[0] || null,
    matchType,
    alternativeReason,
    diagnostics,
    hasMatch: allOptions.length > 0,
    hasSuggestions: suggestions.length > 0,
    suggestions,
    showContactButton: true,
    totalOptionsChecked: preferences.length * monteurs.length,
    availableOptions: allOptions.length
  }
}];

