const payload = prevData.body ?? prevData;

// Converteer type_duur + installatie_duur naar minuten (backup)
function berekenInstallatieDuurMinuten(typeDuur, aantal) {
  const value = parseInt(aantal) || 0;
  const multipliers = {
    'minuten': 1,
    'uren': 60,
    'dagen': 540,    // 9 werkuren per dag (08:00-17:00)
    'weken': 2700    // 5 werkdagen x 9 uur = 45 uur
  };
  const multiplier = multipliers[typeDuur?.toLowerCase()] || 1;
  return value * multiplier;
}

const confirmedSlot = payload.confirmedSlot ?? {};
// Probeer eerst payload, dan bereken uit deal properties
let installatieDuurMinuten = Number(payload.installatieDuurMinuten ?? payload.installatieDuur ?? prevData.installatieDuurMinuten ?? 0);
if (!installatieDuurMinuten && dealResponse.properties?.type_duur && dealResponse.properties?.installatie_duur) {
