if (!confirmedSlot.timeSlot) throw new Error('confirmedSlot.timeSlot ontbreekt (verwacht payload.confirmedSlot.timeSlot)');
if (!installatieDuurMinuten) throw new Error('installatieDuurMinuten ontbreekt of is 0');
if (!customerAddress) throw new Error('customerAddress ontbreekt');

// Eindtijd berekenen
